import React, {useState} from "react";
import Card from "../UI/Card";
import "./Expense.css";
import ExpensesFilter from "./ExpensesFilter";
import ExpensesList from "./ExpensesList";

function Expense(props) {

  const [selectedYear, setSelectedYear] = useState("2020");

  const yearChangeHandler = (yearParameter) => {
    setSelectedYear(yearParameter);
  }

  const filteredItems = props.items.filter(item => {
    return item.date.getFullYear().toString() === selectedYear
  })

  let expensesContent = <p>No content found</p>

  if (filteredItems.length>0) {
    expensesContent=filteredItems.map(expense =>
      <ExpensesList/>
    )
  }

  return (
    <div>
    <Card className="expenses">
    <ExpensesFilter selected={selectedYear} onYearChange={yearChangeHandler}/>
    {expensesContent}
    </Card>
    </div>
  )

}

export default Expense;
